  @Test
  public void tEST1LINKS() {
    driver.get("https://localhost:8080//");
    {
      List<WebElement> elements = driver.findElements(By.xpath("//a[contains(text(),\'Reset\')]"));
      assert (elements.size() > 0);
    }
    {
      WebElement element = driver.findElement(By.linkText("Reset"));
      String attribute = element.getAttribute("href");
      vars.put("linkHref", attribute);
    }
    assertEquals(vars.get("linkHref").toString(), "https://localhost:8080/reset");
  }

  @Test
  public void tEST3CATALOG() {
    driver.get("https://localhost:8080//");
    driver.findElement(By.xpath("//a[text()=\'Catalog\']")).click();
    {
      WebDriverWait wait = new WebDriverWait(driver, Duration.ofSeconds(30));
      wait.until(ExpectedConditions.visibilityOfElementLocated(By.xpath("//ol/li[2]/img")));
    }
    {
      WebElement element = driver.findElement(By.xpath("//ol/li[2]/img"));
      String attribute = element.getAttribute("src");
      vars.put("linkHref", attribute);
    }
    assertEquals(vars.get("linkHref").toString(), "https://localhost:8080/images/cat2.jpg");
  }

  @Test
  public void tEST6RENT() {
    driver.get("https://localhost:8080//reset");
    driver.findElement(By.xpath("//a[text()=\'Rent-A-Cat\']")).click();
    driver.findElement(By.id("rentID")).sendKeys("1");
    driver.findElement(By.xpath("//button[contains(text(),\'Rent\')]")).click();
    {
      List<WebElement> elements = driver
          .findElements(By.xpath("//ul[@class=\'list-group\']/li[1][text()=\'Rented out\']"));
      assert (elements.size() > 0);
    }
    {
      List<WebElement> elements = driver
          .findElements(By.xpath("//ul[@class=\'list-group\']/li[2][text()=\'ID 2. Old Deuteronomy\']"));
      assert (elements.size() > 0);
    }
    {
      List<WebElement> elements = driver
          .findElements(By.xpath("//ul[@class=\'list-group\']/li[3][text()=\'ID 3. Mistoffelees\']"));
      assert (elements.size() > 0);
    }
    assertThat(driver.findElement(By.id("rentResult")).getText(), is("Success!"));
  }

